function loadStories(t){$.ajax({method:"get",url:`https://api.nytimes.com/svc/topstories/v2/${t}.json?api-key=81xlA4cYvKGRAH8yiQiiE2CuHr82DZDM`}).done(function(t){$.each(t.results,function(t,i){if(t<16){let t=i.multimedia[4].url,s=i.abstract;$(".stories").append(`<div class="story" style="background-image: url(${t})"><div class="abstract">${s}</div></div>`)}})}).fail(function(){$(".stories").append("Sorry there was an error.")}).always(function(){$(".loading").hide()})}$("#section-select").on("change",function(){$(".loading").show();const t=$(this).val();$(".stories").html(""),""!==t&&loadStories(t)});