function loadStories(t){$.ajax({method:"get",url:`https://api.nytimes.com/svc/topstories/v2/${t}.json?api-key=81xlA4cYvKGRAH8yiQiiE2CuHr82DZDM`}).done(function(t){console.log(t);const a=t.results.filter(t=>5==t.multimedia.length);$.each(a,function(t,a){if(t<16){let t=a.multimedia[4].url,e=a.url,i=a.abstract;$(".stories").append(`<a href="${e}" target=blanck class="story" style="background-image: url(${t})"><div class="abstract"><p>${i}</p></div></a>`)}})}).fail(function(){$(".stories").append("Sorry there was an error.")}).always(function(){$(".loading-div").hide()})}$("#selector").on("change",function(){$(".loading-div").show(),$("header").addClass("active");const t=$(this).val();$(".stories").html(""),""!==t&&loadStories(t)});